package find_the_difference_389;

import java.util.HashMap;
public class Main {

	public static void main(String args[]) {
		
		String a = "abcd";
		String b = "abcde";
		
		System.out.println(findTheDifference(a, b));
		
		String s = "";
		String t = "";
		System.out.println(findTheDifference(s, t));
	
	}

	public static char findTheDifference(String s, String t) {
		
		HashMap<Character, Integer> map1 = new HashMap<>();
		HashMap<Character, Integer> map2 = new HashMap<>();
		
		for(int i=0; i<s.length(); i++) {
			if(map1.containsKey(s.charAt(i))) {
				int temp = map1.get(s.charAt(i));
				map1.replace(s.charAt(i), temp + 1);
			}
			else map1.put(s.charAt(i), 1);
		}
		
		for(int i=0; i<t.length(); i++) {
			if(map2.containsKey(t.charAt(i))) {
				int temp = map2.get(t.charAt(i));
				map2.replace(t.charAt(i), temp + 1);
			}
			else map2.put(t.charAt(i), 1);
		}
		
		for(int i=0; i<t.length(); i++) {
			if(map1.get(t.charAt(i))!=map2.get(t.charAt(i)))
				return t.charAt(i);
		}
		
		throw new NoSuchMethodError("NO SOLUTIONS");
	}

}
